@tailwind base;
@tailwind components;
@tailwind utilities;

:root {
  --font-sans: 'Inter', sans-serif;
  --font-mono: 'JetBrains Mono', monospace;
  --color-background: #f8f9fa;
  --color-surface: #ffffff;
  --color-surface-muted: #f1f5f9;
  --color-primary-bg: #111827;
  --color-primary-fg: #ffffff;
  --color-text: #1f2937;
  --color-text-muted: #64748b;
  --color-outline: #e5e7eb;
  --color-outline-focus: #1f2937;
  --color-ring: rgba(31, 41, 55, 0.2);
}
@media (prefers-color-scheme: dark) {
  :root {
    --color-background: #0a0a0a;
    --color-surface: #111111;
    --color-surface-muted: #1a1a1a;
    --color-primary-bg: #f8f9fa;
    --color-primary-fg: #0a0a0a;
    --color-text: #f8f9fa;
    --color-text-muted: #9ca3af;
    --color-outline: #374151;
    --color-outline-focus: #a7a7a7;
    --color-ring: rgba(167, 167, 167, 0.2);
  }
}

@layer base {
  body {
    background-color: var(--color-background);
    color: var(--color-text);
    font-family: var(--font-sans);
    @apply antialiased;
  }

  input, select, textarea {
    background-color: var(--color-surface);
    border-color: var(--color-outline);
    color: var(--color-text);
    @apply w-full border rounded-md px-3 py-2 text-base outline-none transition-all duration-200;
  }
  input:focus, select:focus, textarea:focus {
    border-color: var(--color-outline-focus);
    --tw-ring-color: var(--color-ring);
    @apply ring-2;
  }
  select {
    -webkit-appearance: none;
    appearance: none;
    background-image: url("data:image/svg+xml,%3csvg xmlns='http://www.w3.org/2000/svg' fill='none' viewBox='0 0 20 20'%3e%3cpath stroke='%236b7280' stroke-linecap='round' stroke-linejoin='round' stroke-width='1.5' d='M6 8l4 4 4-4'/%3e%3c/svg%3e");
    background-position: right 0.5rem center;
    background-repeat: no-repeat;
    background-size: 1.5em 1.5em;
  }
  input::placeholder, textarea::placeholder { color: var(--color-text-muted); opacity: 0.7; }

  pre code.hljs {
    background-color: var(--color-surface-muted) !important;
    @apply p-4 rounded-b-lg;
  }
}

@layer components {
  ::-webkit-scrollbar { width: 8px; height: 8px; }
  ::-webkit-scrollbar-track { background: var(--color-surface); }
  ::-webkit-scrollbar-thumb { background-color: var(--color-outline); border-radius: 4px; }
  ::-webkit-scrollbar-thumb:hover { background-color: var(--color-outline-hover); }
}

.logo img { filter: var(--color-background) invert(0); }
@media (prefers-color-scheme: dark) { .logo img { filter: var(--color-background) invert(1); } }
